<template>
  <div class="flex flex-col self-start rounded bg-base mt-4 p-4 shadow-inner">
    <h2 class="leading-normal text-lg font-normal mb-1">Order the limited edition canvas:</h2>

    <!-- <p class="text-xs mb-4">
      Please note: Canvas sizes are approximate as each frame is custom built to fit the picture.
    </p> -->

    <div class="bg-white rounded mb-4">
      <div class="font-bold p-3 border-b border-base flex justify-between">
        <div class="flex-1 ml-3">
          18" x 16" (46cm x 41cm)
        </div>
        <div class="flex-1 mx-2">
          Canvas
        </div>
        <div>
          £50.00
        </div>
      </div>
    </div>

    <template>
      <h2 class="leading-normal text-lg font-normal mb-1">
        Delivery:
      </h2>
      
      <div class="bg-white rounded shadow-md mb-4">
        <label
          v-for="(post, index) in postage"
          :key="index"
          :class="[shippingSelected === index ? 'font-bold' :  '']"
          class="p-3 cursor-pointer border-b border-base hover:bg-light flex items-center justify-between"
        >
          <input type="radio" :checked="shippingSelected === index" name="delivery" @change="updateShipping(post.price, index)">
          <div class="flex-1 ml-3">
            {{ post.title }}
          </div>
          <div>
            <span class="ml-2">{{ formatPrice(post.price) }}</span>
          </div>
        </label>
      </div>
    </template>

    <p v-if="error" class="my-1 text-sm text-danger font-bold">{{ error }}</p>

    <div class="flex my-2 justify-between py-2">
      <span class="font-thin text-lg">Total Price</span>

      <span class="font-bold text-lg text-right">
        <template v-if="price">{{ formatPrice(totalPrice) }} <span v-if="!shipping">*</span></template>
        <template v-else>n/a</template>
      </span>
    </div>

    <div class="font-thin text-sm text-right" v-if="price && !shipping">*Not including delivery</div>

    <button
      @click="buy()"
      class="paypal w-full my-2 inline-block rounded py-3 px-6 no-underline items-center flex justify-center "
    >
      Pay with
      <img class="ml-2 h-5" src="data:image/svg+xml;base64," alt="paypal">
    </button>
  </div>
</template>

<script>
const CanvasPostage = [{
  title: "UK",
  price: 9
},
{
  title: "Europe",
  price: 14
},
{
  title: "Rest of the World",
  price: 20
}];

export default {
  computed: {
    totalPrice() {
      return this.shipping + this.price
    },

    paypalUrl() {
      return `https://www.paypal.com/cgi-bin/webscr?cmd=_xclick&business=${this.business}&item_name=${this.name}&amount=${this.price}&currency_code=${this.currencyCode}&shipping=${this.shipping}`;
    },

    postage() {
      return CanvasPostage;
    }
  },

  methods: {
    updateShipping(value, index) {
      this.shipping = value;
      this.shippingSelected = index;
    },

    formatPrice(value) {
      let val = (value / 1).toFixed(2);
      val = val.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ".");
      return `£${val}`;
    },

    buy() {
      // Validate shipping
      if (!this.shipping) {
        this.error = "Please select a delivery option";
        return;
      }

      window.location.href = this.paypalUrl;
    }
  },

  data() {
    return {
      price: 50,
      name: 'Voice of the Tees - Limited Edition (18 x 16)',
      error: false,
      currencyCode: "GBP",
      business: "wilsonfamily@ntlworld.com",
      shipping: null,
      shippingSelected: null,
      type: 'Canvas'
    };
  },

  watch: {
    type() {
      this.shipping = null;
      this.shippingSelected = null;
      this.error = null;
    },
    price() {
      this.error = null;
    },
    shipping() {
      this.error = null;
    }
  }
};
</script>

